import React, { useState } from "react";

function DisplayNames(props) {
  let [search, setSearch] = useState([]);

  function handleSearch(event) {
    const babyName = event.target.value;
    console.log(babyName);

    const newSearch = props.data
      .filter((nameB) => {
        return nameB.name.includes(babyName);
      })
      .map((el) => {
        if (el.sex === "f") {
          return <span className="orange"> {el.name} </span>;
        } else {
          return <span className="blue"> {el.name} </span>;
        }
      });

    setSearch(newSearch);
  }

  //   function trial(l, d) {
  //     if (l.sex === "f") {
  //       return (
  //         <span key={d} className="orange">
  //           {" "}
  //           {l.name}{" "}
  //         </span>
  //       );
  //     } else {
  //       return (
  //         <span key={d} className="blue">
  //           {" "}
  //           {l.name}{" "}
  //         </span>
  //       );
  //     }
  //   }

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Enter Baby Names"
          onChange={handleSearch}
        />
      </div>
      {search.length !== 0 && (
        <p>
          {props.data
            .sort((a, b) => a.name.localeCompare(b.name))
            .map((el, id) => {
              if (el.sex === "f") {
                return (
                  <span key={id} className="orange">
                    {" "}
                    {el.name}{" "}
                  </span>
                );
              } else {
                return (
                  <span key={id} className="blue">
                    {" "}
                    {el.name}{" "}
                  </span>
                );
              }
            })}
        </p>
      )}
    </div>
  );
}

export default DisplayNames;
