import React, { useState } from "react";
import data from "./babyNamesData.json";
import "./App.css";

function App() {
  let [search, setSearch] = useState();

  function DisplayNames() {
    return (
      <p>
        {data
          .sort((a, b) => a.name.localeCompare(b.name))
          .map((el, id) => {
            if (el.sex === "f") {
              return (
                <span key={id} className="orange">
                  {" "}
                  {el.name}{" "}
                </span>
              );
            } else {
              return (
                <span key={id} className="blue">
                  {" "}
                  {el.name}{" "}
                </span>
              );
            }
          })}
      </p>
    );
  }

  function searching(event) {
    let babyName = event.target.value;
    console.log(babyName);

    data
      .filter((nameB) => {
        return nameB.name.includes(babyName);
      })
      .map((el, id) => setSearch(trial(el, el.id)));
  }

  function trial(l, d) {
    if (l.sex === "f") {
      console.log(l);
      console.log(d);
      return (
        <span key={d} className="orange">
          {" "}
          {l.name}{" "}
        </span>
      );
    } else {
      return (
        <span key={d} className="blue">
          {" "}
          {l.name}{" "}
        </span>
      );
    }
  }

  return (
    <div className="App">
      <input type="text" value={search} onChange={searching} />
      <DisplayNames />
    </div>
  );
}

export default App;
