import React, { useState } from "react";
import data from "./babyNamesData.json";
import "./App.css";

function App() {
  let [search, setSearch] = useState();

  function searching(event) {
    let babyName = event.target.value.toLowerCase();
    console.log(babyName);
    setSearch(data.filter);
  }

  return (
    <div className="App">
      <input type="text" value={search} onChange={searching} />
      <p>
        {data
          .sort((a, b) => a.name.localeCompare(b.name))
          .map((el, id) => {
            if (el.sex === "f") {
              return (
                <span key={id} className="orange">
                  {" "}
                  {el.name}{" "}
                </span>
              );
            } else {
              return (
                <span key={id} className="blue">
                  {" "}
                  {el.name}{" "}
                </span>
              );
            }
          })}
      </p>
    </div>
  );
}

export default App;
