import React, { useState } from "react";

function DisplayNames(props) {
  let [search, setSearch] = useState([]);
  let [count, setCount] = useState();

  count = {
    search,
    favorites: [],
  };

  function handleSearch(event) {
    const babyName = event.target.value;
    const newSearch = props.data.filter((nameB) => {
      return nameB.name.toLowerCase().includes(babyName.toLowerCase());
    });

    setSearch(newSearch);
  }

  let favorites;
  const favoriteName = (el) => {
    const search = count.search.filter((i) => i !== el);
    let favorites = count.favorites.concat(el);
    console.log(favorites);
    setCount({ search, favorites });
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Enter Baby Names"
          onChange={handleSearch}
        />
      </div>

      <p>
        {search
          .sort((a, b) => a.name.localeCompare(b.name))
          .map((el, id) => {
            if (el.sex === "f") {
              return (
                <span
                  key={id}
                  className="orange"
                  onClick={() => favoriteName(el)}
                >
                  {" "}
                  {el.name}{" "}
                </span>
              );
            } else {
              return (
                <span
                  key={id}
                  className="blue"
                  onClick={() => favoriteName(el)}
                >
                  {" "}
                  {el.name}{" "}
                </span>
              );
            }
          })}
      </p>
      <div>
        <ol>
          {favorites.map((na) => (
            <li key={na}>{na}</li>
          ))}
        </ol>
      </div>
    </div>
  );
}

export default DisplayNames;
