import React, { useState } from "react";

function DisplayNames(props) {
  const [items, setItems] = useState([
    { id: 1, itemName: "item 1", quantity: 1, isSelected: false },
    { id: 2, itemName: "item 2", quantity: 3, isSelected: true },
    { id: 3, itemName: "item 3", quantity: 2, isSelected: false },
  ]);
  const [search, setSearch] = useState("");
  const [count, setCount] = useState([]);

  function handleSearch(event) {
    setSearch(event.target.value);
  }

  // let favorites;
  const favoriteName = (el) => {
    setCount([...count, el]);
    setItems(items.filter((i) => i.id !== el.id));
  };

  const removeFavoritesName = (el) => {
    setItems([...items, el]);
    setCount(count.filter((i) => i.id !== el.id));
  };

  const filteredItems = items.filter((item) => item.itemName.includes(search));

  return (
    <div>
      <div>
        <h1>BABY NAMES</h1>
        <input
          type="text"
          placeholder="Enter Baby Names"
          value={search}
          onChange={handleSearch}
        />
      </div>

      <p>
        {filteredItems
          .sort((a, b) => a.itemName.localeCompare(b.name))
          .map((el, id) => {
            if (el.sex === "f") {
              return (
                <span
                  key={id}
                  className="orange"
                  onClick={() => favoriteName(el)}
                >
                  {" "}
                  {el.itemName}{" "}
                </span>
              );
            } else {
              return (
                <span
                  key={id}
                  className="blue"
                  onClick={() => favoriteName(el)}
                >
                  {" "}
                  {el.itemName}{" "}
                </span>
              );
            }
          })}
      </p>
      <div>
        <ol>
          {count.map((na) => (
            <li key={na.id} onClick={() => removeFavoritesName(na)}>
              {na.itemName}
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
}

export default DisplayNames;
